package com.mygg;

import javafx.application.Application;
import javafx.scene.Scene;
import javafx.scene.layout.StackPane;
import javafx.stage.Stage;

public class SpritePreview extends Application {

    @Override
    public void start(Stage stage) {
        StackPane sprite = new StackPane();
        sprite.getStyleClass().add("idle_down1");

        StackPane root = new StackPane(sprite);
        Scene scene = new Scene(root, 400, 400);
        scene.getStylesheets().add(
                getClass().getResource("/com/mygg/sprites/sprites.css").toExternalForm()
        );

        stage.setTitle("Sprite Test");
        stage.setScene(scene);
        stage.show();
    }

    public static void main(String[] args) {
        launch();
    }
}
