package com.mygg;
import javafx.application.Application;
import javafx.scene.Scene;
import javafx.scene.canvas.Canvas;
import javafx.scene.canvas.GraphicsContext;
import javafx.scene.image.Image;
import javafx.scene.image.WritableImage;
import javafx.scene.layout.StackPane;
import javafx.stage.Stage;

public class SpriteTest extends Application {

    @Override
    public void start(Stage stage) {

        // Load sprite sheet
        Image sheet = new Image("./BombermanWorldArc-Bomberman.png");

        int tileW = 24;   // width satu sprite
        int tileH = 32;   // height satu sprite

        // Ambil sprite (col, row)
        // Misal ambil sprite paling pertama (0,0)
        Image sprite = getSprite(sheet, 0, 0, tileW, tileH);

        // Canvas untuk menampilkan sprite
        Canvas canvas = new Canvas(200, 200);
        GraphicsContext gc = canvas.getGraphicsContext2D();

        // Gambar sprite di tengah
        gc.drawImage(sprite, 50, 50);

        Scene scene = new Scene(new StackPane(canvas));
        stage.setScene(scene);
        stage.setTitle("Sprite Loader Test");
        stage.show();
    }

    // Fungsi crop sprite dari grid
    public static Image getSprite(Image sheet, int col, int row, int tileW, int tileH) {
        return new WritableImage(
                sheet.getPixelReader(),
                col * tileW,
                row * tileH,
                tileW,
                tileH
        );
    }

    public static void main(String[] args) {
        launch(args);
    }
}
