package com.mygg.entities;

import javafx.scene.canvas.GraphicsContext;
import javafx.scene.image.Image;

public class ItemType {
    public final int tileX;
    public final int tileY;
    public final ItemType type;

    private final Image sprite;
    private final int tileSize = 32;

    // Enum ItemType langsung di sini
    public enum ItemType {
        BOMB_UP,
        SPEED_UP,
        FIRE_UP
    }

    public ItemType(int x, int y, ItemType type) {
        this.tileX = x;
        this.tileY = y;
        this.type = type;

        String path = switch (type) {
            case BOMB_UP -> "/com/mygg/assets/item/BombUp.png";
            case SPEED_UP -> "/com/mygg/assets/item/SpeedUp.png";
            case FIRE_UP -> "/com/mygg/assets/item/FireUp.png";
        };

        sprite = new Image(getClass().getResourceAsStream(path), tileSize, tileSize, false, false);
    }

    public void render(GraphicsContext g) {
        g.drawImage(sprite, tileX * tileSize, tileY * tileSize);
    }
}
