@@ -1,5 +1,64 @@
-package com.mygg.render;
+public class GameRenderer {
 
-public class GameRenderer {
-    
+    private final GameCanvas canvas;
+    private final Player player;
+    private final int[][] map;
+    private final BombManager bombs;
+    private final ExplosionManager explosions;
+    private final CameraScaler scaler;
+
+    private final Image ground, breakable, unbreak;
+
+    public GameRenderer(GameCanvas canvas, Player player, int[][] map,
+                        BombManager bombs, ExplosionManager explosions,
+                        CameraScaler scaler) {
+
+        this.canvas = canvas;
+        this.player = player;
+        this.map = map;
+        this.bombs = bombs;
+        this.explosions = explosions;
+        this.scaler = scaler;
+
+        ground    = new Image("/com/mygg/assets/tiles/ground.png");
+        breakable = new Image("/com/mygg/assets/tiles/break.png");
+        unbreak   = new Image("/com/mygg/assets/tiles/unbreak.png");
+    }
+
+    public void render() {
+        GraphicsContext g = canvas.getGraphicsContext2D();
+        g.clearRect(0, 0, canvas.getWidth(), canvas.getHeight());
+
+        g.save();
+        scaler.applyTransform(g);
+
+        renderMap(g);
+        bombs.render(g);
+        explosions.render(g);
+        g.drawImage(player.update(1/60.0), player.x, player.y);
+
+        g.restore();
+        renderUI(g);
+    }
+
+    private void renderMap(GraphicsContext g) {
+        int tile = 32;
+        for (int y = 0; y < map[0].length; y++) {
+            for (int x = 0; x < map.length; x++) {
+                Image img = switch (map[x][y]) {
+                    case 1 -> unbreak;
+                    case 2 -> breakable;
+                    default -> ground;
+                };
+                g.drawImage(img, x * tile, y * tile);
+            }
+        }
+    }
+
+    private void renderUI(GraphicsContext g) {
+        g.save();
+        g.setFont(new Font("Consolas", 24));
+        g.fillText(player.timer.toString(), canvas.getWidth() / 2 - 40, 30);
+        g.restore();
+    }
 }

